sysauto_script.script:
//Creates a variable for the system property of auto-close timing in days
var howLongToClose = gs.getProperty('x_57782_needit.autoCloseOverdue');

//Creates a new record query from the needit_task table, looking for records where the due date is further in the past than the auto-close timing system property, where the state is not 'closed complete'(3)
var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date', '<=', gs.daysAgo(howLongToClose));
overdueNITasks.addQuery('state','<',3);

//Execute the query
overdueNITasks.query();

//Cycle through all records in query: for all matching records, print a line to log stating that the due date has lapsed for the record, then set the state to 'closed incomplete' (4)
while(overdueNITasks.next()){
	//Add a note to the system log stating the NeedIt Task is being closed
	gs.info('Auto close NeedIt Task record due to service-window lapse= ' + overdueNITasks.number);

	//Generate an event for each overdue NeedIt task record
	gs.eventQueue('x_58872_needit.overdueNeedItTask', overdueNITasks, overdueNITasks.number, gs.getUserName());

	//Update the record's fields to close the task with notes added
	overdueNITasks.work_notes = 'Record closed because it was too far past the due date.';
	overdueNITasks.state = 4;

	//Final command to push the update
	overdueNITasks.update();
}
