sysauto_script.script:
//Variable for today's date
//var today = new GlideDateTime();

//Creates a new record query from the needit_task table, looking for records where the due date is within 7 days of the due date, where the state is not closed
var duesoonNITasks = new GlideRecord('x_58872_needit_needit_task');
duesoonNITasks.addQuery('due_date','<',gs.daysAgo(-7));
duesoonNITasks.addQuery('state','<',3);

//Execute the query
duesoonNITasks.query();

//Cycle through all records in query: for all matching records, lodge an event that a task is due soon
while(duesoonNITasks.next()){
	//Add a note to the system log stating the NeedIt Task is being closed
	gs.info('NeedIt Task record due in 2 days ' + duesoonNITasks.number);

	//Generate an event for each due soon NeedIt task record
	gs.eventQueue('x_58872_needit.duesoonNeedItTask', duesoonNITasks, duesoonNITasks.number, gs.getUserName());

	//Final command to push the update
	duesoonNITasks.update();
}
